import React, { useEffect } from "react";
import CssBaseline from "@material-ui/core/CssBaseline";
import Container from "@material-ui/core/Container";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Paper from "@material-ui/core/Paper";
import APIService from "../../service/APIService";
import { Button, Divider } from "@material-ui/core";
import FormControl from "@material-ui/core/FormControl";
import InputLabel from "@material-ui/core/InputLabel";
import Select from "@material-ui/core/Select";

const useStyles = makeStyles((theme) => ({
  root: {
    "& > *": {
      margin: theme.spacing(1),
      width: "25ch",
    },
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));

export default function SimpleContainer() {
  const classes = useStyles();
  const [answer, setAnswer] = React.useState("");
  const [current, setCurrent] = React.useState(0);

  const [point, setPoint] = React.useState(0);
  const [questions, setquestions] = React.useState();
  const [allAnswers, setAllAnswers] = React.useState([]);

  useEffect(() => {
    //useEffect bazidan wamoghebis magaliti
    APIService.getQuestion("normal").then((res) => {
      console.log(res.data);
      setquestions(res.data);
    });
  }, [setquestions]);
  const handleAnswerChange = (event) => {
    setAnswer(event.target.value);
  };

  const onAnswer = () => {
    setCurrent(current + 1);
    setAnswer("");
    if (answer.toLowerCase() === questions[current].answer.toLowerCase()) {
      setPoint(point + 2);
    }
    console.log(point);

    setAllAnswers([...allAnswers, answer]);
  };
  const onComplete = () => {
    const all = [...allAnswers, answer];
    if (answer.toLowerCase() === questions[current].answer.toLowerCase()) {
      setPoint(point + 2);
    }
    console.log(all);
    setAllAnswers(all);
  };

  return (
    <React.Fragment>
      <CssBaseline />
      <Container
        maxWidth="sm"
        style={{
          backgroundColor: "#fcba03",
          height: "85vh",
          marginTop: "15px",
          paddingBottom: "15px",
        }}
      >
        <h2 style={{ textAlign: "center" }}>Lets Play</h2>
        <FormControl className={classes.formControl}>
          <InputLabel id="demo-simple-select-label">Difficulity</InputLabel>
          <Select
            labelId="demo-simple-select-label"
            value={level}
            onChange={handleChange}
          >
            <MenuItem value={"easy"}>Easy</MenuItem>
            <MenuItem value={"normal"}>Normal</MenuItem>
            <MenuItem value={"hard"}>Hard</MenuItem>
          </Select>
        </FormControl>
        <div>
          {questions && (
            <Paper>
              <p>{questions[current].question}</p>
              <TextField
                label="Write answer"
                value={answer}
                fullWidth
                onChange={handleAnswerChange}
              />
              {current !== questions.length - 1 ? (
                <Button onClick={onAnswer}>Submit</Button>
              ) : (
                <Button onClick={onComplete}>Finish</Button>
              )}
            </Paper>
          )}
        </div>
      </Container>
    </React.Fragment>
  );
}
